import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route } from 'react-router-dom';
import PrivateRoute from '../src/Components/PrivateRoute';

import DashboardSidebar from '../src/Components/DashboardSidebar';
import { LoginPage } from '../src/Pages/Login/LoginPage';

import 'semantic-ui-css/semantic.min.css';
import '../src/styles/App.css';
import 'antd/dist/antd.css';


function App() {
  const [loginDetails, setLoginDetails] = useState({});

  useEffect(() => {
    return () => {
      window.localStorage.setItem('GofarDashboardToken',loginResult.authToken);
      window.localStorage.setItem('GofarDashboardUserId',loginResult.userId);
      const [localUserId] = useState(localStorage.getItem('GofarDashboardUserId'));
      const [localToken] = useState(localStorage.getItem('GofarDashboardToken'));
      // localStorage.getItem('GofarDashboardUserId')
      // localStorage.getItem('GofarDashboardToken'))

    };
  });

  return (
    <>
      <BrowserRouter>
        <div>
          <PrivateRoute
            component={DashboardSidebar}
            userId={localUserId}
            authToken={localToken}
            exact
            path="/"
          />
          <Route
            render={props => (
              <LoginPage
                {...props}
                handleLoggedIn={loginResult => {
                  // TODO - you have auth token and user ID now.
                  setLoginDetails(loginResult);
                }}
                authToken={loginDetails.authToken}
              />
            )}
            exact
            path="/login"
          />
        </div>
      </BrowserRouter>
    </>
  );
}

export default App;
