import React, { useState } from 'react';
import nookies from 'nookies';
import { BrowserRouter, Route } from 'react-router-dom';
import PrivateRoute from '../src/Components/PrivateRoute';

import DashboardSidebar from '../src/Components/DashboardSidebar';
import { LoginPage } from '../src/Pages/Login/LoginPage';

function App() {
  const [loginDetails, setLoginDetails] = useState({});
  nookies.set(NookieUserId, 'GofarDashboardToken', '', {
    maxAge: 30 * 24 * 60 * 60,
    path: '/',
  });
  console.log(nookies.get(NookieUserId));
  return (
    <>
      <BrowserRouter>
        <div>
          <PrivateRoute
            component={DashboardSidebar}
            userId={nookies.get(NookieUserId)}
            authToken={nookies.get(NookieToken)}
            exact
            path="/"
          />
          <Route
            render={props => (
              <LoginPage
                {...props}
                handleLoggedIn={loginResult => {
                  // TODO - you have auth token and user ID now.
                  setLoginDetails(loginResult);
                  nookies.set(
                    NookieUserId,
                    'GofarDashboardUserId',
                    loginResult.userId,
                    {
                      maxAge: 30 * 24 * 60 * 60,
                      path: '/',
                    }
                  );
                  nookies.set(
                    NookieToken,
                    'GofarDashboardToken',
                    loginResult.userId,
                    {
                      maxAge: 30 * 24 * 60 * 60,
                      path: '/',
                    }
                  );
                  // 'GofarDashboardUserId',
                  // 'GofarDashboardToken',
                  // loginResult.userId
                  // loginResult.authToken
                }}
                authToken={loginDetails.authToken}
              />
            )}
            exact
            path="/login"
          />
        </div>
      </BrowserRouter>
    </>
  );
}

export default App;
